import React, { Component } from 'react';

const initData = {
    pre_heading: "Developers",
    heading: "Live Help",
    btnText: "View All"
}

const data = [
    {
        id: "1",
        img: "/img/max.png",
        // date: "2021-12-09",
        title: "Java Expert",
        verified: "/img/yellowstar.png",
        seller_thumb: "/img/github.png",
        seller: "@Richard",
        btnText: "Ask For Help"

        // price: "1.5 BNB",
        // count: "1 of 1"
    },
    {
        id: "2",
        img: "/img/auction_2.jpg",
        date: "2021-10-05",
        title: "C# Expert",
        verified: "/img/yellowstar.png",
        seller_thumb: "/img/avatar_2.jpg",
        seller: "@JohnDeo",
        btnText: "Ask For Help"
        // count: "1 of 1"
    },
    {
        id: "3",
        img: "/img/auction_3.jpg",
        date: "2021-09-15",
        title: "C++ Expert",
        verified: "/img/yellowstar.png",
        seller_thumb: "/img/max.png",
        seller: "@MKHblots",
        btnText: "Ask For Help"
        // count: "1 of 1"
    },
    {
        id: "4",
        img: "/img/auction_4.jpg",
        date: "2021-12-29",
        title: "Python Expert",
        verified: "/img/yellowstar.png",
        seller_thumb: "/img/avatar_4.jpg",
        seller: "@RioArham",
        btnText: "Ask For Help"
        // count: "1 of 1"
    },
    {
        id: "5",
        img: "/img/auction_5.jpg",
        date: "2022-01-24",
        title: "Javascript Expert",
        verified: "/img/yellowstar.png",
        seller_thumb: "/img/avatar_5.jpg",
        seller: "@ArtNox",
        btnText: "Ask For Help"
        // count: "1 of 1"
    },
    {
        id: "6",
        img: "/img/max.png",
        // date: "2022-03-30",
        title: "React Expert",
        verified: "/img/yellowstar.png",
        seller_thumb: "/img/max.png",
        seller: "@Junaid",
        btnText: "Ask For Help"
        // count: "1 of 1"
    }
]

class AuctionsOne extends Component {
    state = {
        initData: {},
        data: []
    }
    componentDidMount(){
        this.setState({
            initData: initData,
            data: data
        })
    }
    render() {
        return (
            <section className="live-auctions-area">
                <div className="container">
                    <div className="row">
                        <div className="col-12">
                            {/* Intro */}
                            <div className="intro d-flex justify-content-between align-items-end m-0">
                                <div className="intro-content">
                                    <span>{this.state.initData.pre_heading}</span>
                                    <h3 className="mt-3 mb-0">{this.state.initData.heading}</h3>
                                </div>
                                <div className="intro-btn">
                                    <a className="btn content-btn" href="/auctions">{this.state.initData.btnText}</a>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="auctions-slides">
                        <div className="swiper-container slider-mid items">
                            <div className="swiper-wrapper">
                                {/* Single Slide */}
                                {this.state.data.map((item, idx) => {
                                    return (
                                        <div key={`auc_${idx}`} className="swiper-slide item">
                                            <div className="card">
                                                <div className="image-over">
                                                    <a href="/item-details">
                                                        <img className="card-img-top" src={item.img} alt="" />
                                                    </a>
                                                </div>
                                                {/* Card Caption */}
                                                <div className="card-caption col-12 p-0">
                                                    {/* Card Body */}
                                                    <div className="card-body">
                                                        <div className="countdown-times mb-3">
                                                            <div className="countdown d-flex justify-content-center" data-date={item.date} />
                                                        </div>
                                                        <a href="/item-details" className="container-fluid">
                                                            <h5 className=" fa-pull-left">{item.title}</h5>
                                                            <img className="star-sm" src={item.verified} alt="" />

                                                        </a>
                                                        <a className="seller d-flex align-items-center my-3" href="/item-details">
                                                            <img className="avatar-sm rounded-circle" src={item.seller_thumb} alt="" />
                                                            <span className="ml-2">{item.seller}</span>
                                                        </a>
                                                        <div className="card-bottom center-button">
                                                         <a className="btn btn-bordered-white btn-smaller mt-3" href="/wallet-connect">{/*<i className="icon-help mr-2" />*/}{item.btnText}</a>

                                                            <span>{item.price}</span>
                                                            <span>{item.count}</span>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    );
                                })}
                            </div>
                            <div className="swiper-pagination" />
                        </div>
                    </div>
                </div>
            </section>
        );
    }
}

export default AuctionsOne;
